import { createApp } from 'vue';import App from './App.vue'; // Убедитесь, что путь к файлу App.vue верный
import router from './router'; // Если используете vue-router, импортируйте его
import axios from 'axios';

// Настройка Axios
axios.defaults.baseURL = 'http://localhost:3000/api'; // Пример базового URL
axios.interceptors.request.use(
  config => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  error => Promise.reject(error)
);

Vue.prototype.$axios = axios; // Делаем axios доступным во всех компонентах через this.$axios

new Vue({
  router, // Указываем роутер, если он используется
  render: h => h(App)
}).$mount('#app');  // Этот селектор должен соответствовать ID элемента в вашем index.html
